import React, { useState } from 'react';
import './App.css';
import ZombieFighters from './component/ZombieFighters';
import Money from './component/Money';
import Stats from './component/Stats';
import TeamList from './component/TeamList';

const App = () => {
  const initialZombieFighters = [
    { name: 'Survivor', price: 12, strength: 6, agility: 4, img: 'data:image/jpeg;base64,' },
    { name: 'Scavenger', price: 10, strength: 5, agility: 5, img: 'data:image/jpeg;base64,' },
    { name: 'Shadow', price: 18, strength: 7, agility: 8, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT4Na3d_uK46MXVIyejsIRq3svbHGdO1VDRKA&s' },
    { name: 'Tracker', price: 14, strength: 7, agility: 6, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTJQ5a2Ps39mjMUfhaawq2jNGNmOOLAenmNVQ&s' },
    { name: 'Sharpshooter', price: 20, strength: 6, agility: 8, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQWCXJhR_MjRz3tA-ZWu0cIBRUK_2Tnl7TI4g&s' },
    { name: 'Medic', price: 15, strength: 5, agility: 7, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTDcl3fntmH4MbodxUrwgff7FO_d3Y9S31pnA&s' },
    { name: 'Engineer', price: 16, strength: 6, agility: 5, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQvWX4WctkAih9WTqgcqWoBlVwgrpuBmeiqOA&s' },
    { name: 'Brawler', price: 11, strength: 8, agility: 3, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQtXT9IUyc-Njrwg2ZfvsKW64_z2AexeJJ6Aw&s' },
    { name: 'Infiltrator', price: 17, strength: 5, agility: 9, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRw8ateFolAf60FJTjItxqH-fufI5VYQhIBFw&s' },
    { name: 'Leader', price: 22, strength: 7, agility: 6, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSw-7Y6QmDogsWEiBJ6XhcloFRnlY9TyFyNjg&s' },
  ];

  const [team, setTeam] = useState([]);
  const [money, setMoney] = useState(100);
  const [zombieFighters] = useState(initialZombieFighters);

  const calculateTotalStrength = (team) => team.reduce((total, fighter) => total + fighter.strength, 0);
  const calculateTotalAgility = (team) => team.reduce((total, fighter) => total + fighter.agility, 0);

  const handleAddFighter = (fighter) => {
    if (money >= fighter.price) {
      setTeam([...team, fighter]);
      setMoney(money - fighter.price);
    } else {
      console.log("Not enough money");
    }
  };

  const handleRemoveFighter = (index) => {
    const removedFighter = team[index];
    const newTeam = team.filter((_, i) => i !== index);
    setTeam(newTeam);
    setMoney(money + removedFighter.price);
  };

  return (
    <div>
      <h1>Zombie Fighters</h1>

      <Stats team={team} />

      <Money money={money} />

      <TeamList team={team} onRemoveFighter={handleRemoveFighter} />

      <ZombieFighters 
        fighters={zombieFighters} 
        onAddFighter={handleAddFighter} 
      />
    </div>
  );
};

export default App;
